using Microsoft.EntityFrameworkCore;
using Microsoft.AspNetCore.Identity.EntityFrameworkCore;

namespace backend.Data;

public class DatabaseContext : IdentityDbContext
{
    public DatabaseContext(DbContextOptions o) : base(o)
    {
    }
    protected override void OnModelCreating(ModelBuilder modelBuilder)
    {
        base.OnModelCreating(modelBuilder);
        modelBuilder.Entity<Deelnemer>().ToTable("Deelnemers");

        //band *--* deelnemer relation
        modelBuilder.Entity<DeelnemerMetBand>()
            .HasKey(db => new { db.BandId, db.DeelnemerId });
        modelBuilder.Entity<DeelnemerMetBand>()
            .HasOne(db => db.Band)
            .WithMany(b => b.DeelnemersMetBands)
            .HasForeignKey(db => db.BandId);
        modelBuilder.Entity<DeelnemerMetBand>()
            .HasOne(db => db.Deelnemer)
            .WithMany(d => d.DeelnemersMetBands)
            .HasForeignKey(db => db.DeelnemerId);

        //band *--* evenement relation
        modelBuilder.Entity<Opvoering>()
            .HasKey(o => new { o.BandId, o.EvenementId });
        modelBuilder.Entity<Opvoering>()
            .HasOne(o => o.Evenement)
            .WithMany(e => e.Opvoeringen)
            .HasForeignKey(o => o.EvenementId);
        modelBuilder.Entity<Opvoering>()
            .HasOne(o => o.Band)
            .WithMany(b => b.Opvoeringen)
            .HasForeignKey(o => o.BandId);
        
        //gebruiker *--* ticket relation
        modelBuilder.Entity<Ticket>()
            .HasKey(t => new { t.Id});
        modelBuilder.Entity<Ticket>()
            .HasOne(t => t.Gebruiker)
            .WithMany(g => g.Tickets)
            .HasForeignKey(o => o.GebruikerId);
        modelBuilder.Entity<Ticket>()
            .HasOne(t => t.Evenement)
            .WithMany(e => e.Tickets)
            .HasForeignKey(t => t.EvenementId);
        
        //ticket *--1 rang relation
        modelBuilder.Entity<Ticket>()
            .HasOne(t => t.Rang);

        //evenement *--1 zaal relation
        modelBuilder.Entity<Zaal>()
            .HasMany(z => z.Evenementen)
            .WithOne(e => e.Zaal)
            .HasForeignKey(e => e.ZaalId);
        
        //zaal 1--* rang relation
        modelBuilder.Entity<Zaal>()
            .HasMany(z => z.Rangen)
            .WithOne(r => r.Zaal)
            .HasForeignKey(r => r.ZaalId);
        
        //seed
        modelBuilder.Entity<Gebruiker>().HasData(
            new Gebruiker { AantalGedoneerd = 0, UserName = "Sander", Email = "sander@sander.com", Id = "5bbcb86b-c65e-4af6-9e8c-e1bdde530710", PasswordHash = "AQAAAAEAACcQAAAAEOMPg69pmtQA24NFoddZw5HRPLtJOi3byFGBI8eSoxn1yw3EG7h8C9XvyMHVMae7Og==", }
        );

        modelBuilder.Entity<Zaal>().HasData(
            new Zaal { Id = 13, Toegankelijk = true },
            new Zaal { Id = 14, Toegankelijk = true },
            new Zaal { Id = 15, Toegankelijk = true },
            new Zaal { Id = 16, Toegankelijk = true }
        );
        
        modelBuilder.Entity<Rang>().HasData(
            new Rang{Id = 1, Capaciteit = 20, ZaalId = 13},
            new Rang{Id = 2, Capaciteit = 100, ZaalId = 13},
            new Rang{Id = 3, Capaciteit = 120, ZaalId = 13},
            new Rang{Id = 4, Capaciteit = 20, ZaalId = 14},
            new Rang{Id = 5, Capaciteit = 160, ZaalId = 14},
            new Rang{Id = 6, Capaciteit = 10, ZaalId = 15},
            new Rang{Id = 7, Capaciteit = 80, ZaalId = 15},
            new Rang{Id = 8, Capaciteit = 40, ZaalId = 16},
            new Rang{Id = 9, Capaciteit = 200, ZaalId = 16},
            new Rang{Id = 10, Capaciteit = 200, ZaalId = 16}
        );
        
        modelBuilder.Entity<Evenement>().HasData(
            new Evenement{Id = 1, Datum = new DateTime(2023, 6, 25, 16, 00, 00), titel = "lion king", beschrijving = "the lion king musical", ZaalId = 13, img = "data:image/png;base64,", alt = "De lion king poster"},
            new Evenement{Id = 2, Datum = new DateTime(2023, 6, 26, 16, 00, 00), titel = "Hamilton", beschrijving = "An American Musical", ZaalId = 13, img = "data:image/jpeg;base64,", alt = "Hamilton poster"}
        );
    }

    public DbSet<Gebruiker> Gebruikers { get; set; }
    public DbSet<Deelnemer> Deelnemers { get; set; }
    public DbSet<Band> Bands { get; set; }
    public DbSet<DeelnemerMetBand> DeelnemerMetBands { get; set; }
    public DbSet<Evenement> Evenementen { get; set; }
    public DbSet<Ticket> Tickets { get; set; }
    public DbSet<Zaal> Zalen { get; set; }
    public DbSet<Rang> Rangen { get; set; }
}
